// /**
//  * Sample React Native App
//  * https://github.com/facebook/react-native

import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, FlatList, Dimensions, Button, Table, Rows, Row } from 'react-native';
import { SwipeablePanel } from 'rn-swipeable-panel';
import DropDownPicker from 'react-native-dropdown-picker';


export default function App() {
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 20,
      alignItems: 'center',
      justifyContent: 'center'
    },
    title: {
      fontSize: 32,
    },
    life: {
      fonSize:64,
      textAlign: 'center'
    }
  });

  /**
   * App level constants
   */
  const screenWidth  = Dimensions.get('window').width;
  const screenHeight = Dimensions.get('window').height;
  const lifeGridArr  = new Array(numPlayers).fill({life: 40});
  const nameList = [
    'A',
    'B',
    'C',
    'D',
    'E',
    'F',
    'G'
  ];

  const colorList = [
    '#84DCC6',
    '#FE938C',
    '#EAC435',
    '#157145',
    '#4B4E6D',
    '#FFA9E7 ',
    '#BA274A'
  ];

  /**
   *  App level internal state
   */
  const [numPlayers, setNumPlayers] = useState(5);
  const [tileHeight, setTileHeight] = useState(screenHeight / (numPlayers));
  const [tileWidth, setTileWidth]   = useState(screenWidth / 2);
  const [lifeGrid, setLifeGrid]     = useState(lifeGridArr);

  /**
   *  Setting's panel config
   */
  const [panelProps, setPanelProps] = useState({
    fullWidth: true,
    openLarge: false,
    showCloseButton: true,
    onClose: () => setIsPanelActive(false),
    onPressCloseButton: () => setIsPanelActive(false),
  });
  const [isPanelActive, setIsPanelActive] = useState(false);

  /**
   *  Setting's panel internal states
   */
  const [numPlayersSelectOpen, setNumPlayersSelectOpen] = useState(false);
  const [items, setItems] = useState([
    {label: 'Apple', value: 'apple'},
    {label: 'Banana', value: 'banana'}
  ]);


  useEffect(() => {
    setTileHeight(screenHeight / (numPlayers / (numPlayers % 2 === 0 ? 1.5 : 1.25)));
    setLifeGrid(new Array(numPlayers).fill().map(function(_, idx) { return {life: 40, playerName: nameList[idx], color: colorList[idx]}; }));
  }, [numPlayers]);


  const generateColor = () => {
    const randomColor = Math.floor(Math.random() * 16777215)
      .toString(16)
      .padStart(6, '0');
    return `#${randomColor}`;
  };


  const generateCmdrDmgTable = (idx, oddLast) => {
    let cmdDmgHeader = [];
    let cmdDmgTable = [];

    for (var player = 0; player < numPlayers; player++) {
      cmdDmgHeader.push(` ${nameList[player]} `);
      if (idx === player){
        cmdDmgTable.push(' m ');
      } else {
        cmdDmgTable.push(`${Math.floor(Math.random() * 21).toString().padStart(2, ' ').padEnd(3, ' ')}`);
      }
    }

    return(
      // <Table borderStyle={{borderWidth: 1, borderColor: 'grey'}}>
      //   <Row data={cmdDmgHeader}/>
      //   <Row data={cmdDmgTable}/>
      // </Table>
      <>
        <Text style={{
          transform: [{ 
            rotate: oddLast ? '0deg' : 
              idx % 2 === 0 ? '90deg' : '-90deg'
            }], 
          textAlign: 'center',
          fontSize: 18,
          textAlignVertical: 'bottom',
          flex: 1
        }}>
          {cmdDmgHeader}{'\n'}
        </Text>
        <Text style={{
          transform: [{ 
            rotate: oddLast ? '0deg' : 
              idx % 2 === 0 ? '90deg' : '-90deg'
            }], 
          textAlign: 'center',
          fontSize: 18,
          textAlignVertical: 'bottom',
          flex: 1
        }}>
          {cmdDmgTable}
        </Text>
        </>
      );
  };


  const Item = ({ item, oddLast }) => (
    <View style={{backgroundColor: `${item.item.color}`, height: tileHeight, width: oddLast ? screenWidth : tileWidth}}>
      <Text style={{
        transform: [{ 
          rotate: oddLast ? '0deg' : 
            item.index % 2 === 0 ? '90deg' : '-90deg'
          }], 
        textAlign: 'center',
        fontSize: 52,
        textAlignVertical: 'center',
        flex: 1,
      }}>
      <Text style={{
        transform: [{ 
          rotate: oddLast ? '0deg' : 
            item.index % 2 === 0 ? '90deg' : '-90deg'
          }], 
        textAlign: 'center',
        fontSize: 32,
        textAlignVertical: 'center',
        flex: 1,
      }}>
        {item.item.playerName}{'\n'}
      </Text>
        {item.item.life}{'\n'}
        {generateCmdrDmgTable(item.index, oddLast)}
      </Text>
{/*      <Button
        onPress={(e) => { console.log(e); }}
        title='Button'
      />*/}
    </View>
  );


  const renderGridCell = (item) => {
    console.log(item);
    return <Item item={item} oddLast={(item.index === (numPlayers - 1) && numPlayers % 2 !== 0)}/>;
  }


  return (
    <>
    <View style={styles.container}>
      <Text style={{fontSize: 32, textAlign: 'center'}}>MTG Life Counter</Text>
    </View>
    <FlatList data={lifeGrid} renderItem={renderGridCell} numColumns={2}/>
    <View style={[styles.container, {marginTop: '-10%'}]}>
      <Button
        onPress={(e) => { setIsPanelActive(!isPanelActive); }}
        title='Settings'
      />
      <SwipeablePanel {...panelProps} isActive={isPanelActive}>
        <DropDownPicker
          open={open}
          value={value}
          items={items}
          setOpen={setOpen}
          setValue={setValue}
          setItems={setItems}
        />
{/*        <Button
          onPress={(e) => { setNumPlayers(numPlayers + 1); }}
          title='Add Player'
        />
        <Button
          onPress={(e) => { setNumPlayers(numPlayers - 1); }}
          title='Remove Player'
        />*/}
      </SwipeablePanel>
    </View>
    </>
  );
}

